"use strict";
// Explicit type annotation
let myName = "Lorena";
let age = 35;
let isLoggedIn = true;
console.log("Name:", myName);
console.log("Age:", age);
console.log("Logged in:", isLoggedIn);
let city = "Guadalajara"; // inferred as string
let temperature = 38; // inferred as number
let hasAccount = false; // inferred as boolean
console.log("City:", city);
console.log("Temperature:", temperature);
console.log("Has account:", hasAccount);
